var currentInfected,currentKnowledgeBonus,infected=1,viralKnowledge=0,currentViralKnowledge=1,knowledgeBonus=0;function increaseInfected(){infected=Math.ceil(1.1*infected),document.getElementById("infected").innerHTML=infected}function treatInfected(){currentInfected=+document.getElementById("infected").innerHTML,(currentKnowledgeBonus=+document.getElementById("knowledge_bonus").innerHTML)<=0&&(currentKnowledgeBonus=1),(infected=currentInfected-Math.round(1*currentKnowledgeBonus))<0&&(infected=1),document.getElementById("infected").innerHTML=infected}function research(){currentViralKnowledge=+document.getElementById("viral_knowledge").innerHTML,viralKnowledge=Math.ceil(currentViralKnowledge+1),document.getElementById("knowledge_bonus").innerHTML=(1+viralKnowledge/100).toFixed(2),document.getElementById("viral_knowledge").innerHTML=viralKnowledge}var fans=0,fakeFans=0;function fanClick(e){fans+=e,document.getElementById("fans").innerHTML=fans}function buyFan(){var e=Math.floor(10*Math.pow(1.1,fakeFans));fans>=e&&(fakeFans+=1,fans-=e,document.getElementById("fakeFans").innerHTML=fakeFans,document.getElementById("fans").innerHTML=fans);var n=Math.floor(10*Math.pow(1.1,fakeFans));document.getElementById("fanCost").innerHTML=n}window.setInterval((function(){increaseInfected()}),1e3);